Example:

sub clear_login_failures {
    my ($self, $clear_all) = shift;
    my $query = "DELETE FROM login_failure WHERE user_id = ?";
    my @values = ($self->id);
    unless ($clear_all) {
        my $ip_addr = remote_ip();
        trick_taint($ip_addr);
        $query += " AND ip_addr = ?";
        push(@values, $ip_addr);
    }
    Bugzilla->dbh->do($query, undef, @values);
    $clear_all ? delete $self->{account_ip_login_failures};
}

