@@ -693,7 +693,7 @@

       <property name="scroll">
         <setter><![CDATA[
-          // How much pixels spans a single day
+          // How many pixels are spanned by a single day
           var oneday = this.contentWidth;
           if (oneday <= 0) {
               return val;

